export const FETCH_RANDOM_GREETING = 'FETCH_RANDOM_GREETING';

export const fetchRandomGreeting = () => async dispatch => {
  try {
    const response = await fetch('http://localhost:3000/random_greeting'); // Replace with your actual API endpoint
    const data = await response.json();
    dispatch({
      type: FETCH_RANDOM_GREETING,
      payload: data.greeting,
    });
  } catch (error) {
    console.error('Error fetching greeting:', error);
  }
};
